CREATE OR REPLACE FUNCTION tx_obter_qt_trat_pac ( nr_seq_receptor_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_tratamento_w		bigint;


BEGIN

qt_tratamento_w	:= 0;

if (nr_seq_receptor_p IS NOT NULL AND nr_seq_receptor_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qt_tratamento_w
	from	paciente_tratamento a
	where	coalesce(a.dt_final_tratamento::text, '') = ''
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.nr_seq_receptor = nr_seq_receptor_p
	and	substr(tx_obter_se_paciente_ativo(a.nr_seq_receptor),1,1) = 'S';

	if (qt_tratamento_w > 1) then
		qt_tratamento_w	:= 1;
	end if;

end if;

return qt_tratamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
