CREATE OR REPLACE FUNCTION tx_obter_se_lista_servico ( nr_seq_receptor_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_lib_lista_w		bigint;
qt_liberacao_lista_w		bigint;
ie_tipo_prof_lib_regra_w	varchar(15);

ie_lista_servico_w		varchar(1);


c01 CURSOR FOR
	SELECT	ie_tipo_prof_lib
	from	tx_regra_lib_lista
	where	ie_libera_lista_servico = 'S';


BEGIN

ie_lista_servico_w	:= 'N';

select	count(*)
into STRICT	qt_regra_lib_lista_w
from	tx_regra_lib_lista
where	ie_libera_lista_servico = 'S';

select	count(*)
into STRICT	qt_liberacao_lista_w
from	tx_liberacao_lista
where	nr_seq_receptor = nr_seq_receptor_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(ie_situacao,'A') = 'A';

open C01;
loop
fetch c01 into
	ie_tipo_prof_lib_regra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_lista_servico_w
	from	tx_liberacao_lista
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(ie_situacao,'A') = 'A'
	and	nr_seq_receptor = nr_seq_receptor_p
	and	ie_tipo_prof_lib = ie_tipo_prof_lib_regra_w;

	if (ie_lista_servico_w = 'N') then
		exit;
	end if;
	end;
end loop;
close c01;

return	ie_lista_servico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
