SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_se_pessoa_orgao (cd_pessoa_fisica_p text, ie_doador_receptor_p text, ie_tipo_orgao_p text) RETURNS varchar AS $body$
DECLARE




ie_possui_orgao_w	varchar(1)	:= 'N';


BEGIN

if (ie_doador_receptor_p = 'D') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_orgao_w
	from	tx_doador a,
		tx_doador_orgao b,
		tx_orgao c
	where	a.nr_sequencia = b.nr_seq_doador
	and	b.nr_seq_orgao = c.nr_sequencia
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	c.ie_orgao	= ie_doador_receptor_p;

elsif (ie_doador_receptor_p = 'R') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_orgao_w
	from	tx_receptor a,
		tx_orgao c
	where	a.nr_seq_orgao = c.nr_sequencia
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	c.ie_orgao	= ie_doador_receptor_p;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
