CREATE OR REPLACE FUNCTION tx_obter_ultimo_exame_meld (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


dt_exame_w	timestamp;
nr_seq_meld_w	bigint;

BEGIN

select 	max(dt_exame)
into STRICT	dt_exame_w
from	tx_meld
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (dt_exame_w IS NOT NULL AND dt_exame_w::text <> '') then

	select 	max(nr_Sequencia)
	into STRICT	nr_seq_meld_w
	from	tx_meld
	where	dt_exame = dt_exame_w
	and	cd_pessoa_fisica = cd_pessoa_fisica_p;


end if;

return coalesce(nr_seq_meld_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
