CREATE OR REPLACE FUNCTION ub_obter_estrut_conta_conv (nr_seq_protocolo_p bigint, ie_emite_conta_p text) RETURNS bigint AS $body$
DECLARE

 
 
cd_convenio_w	integer;
cd_retorno_w	integer;

 

BEGIN 
 
select	a.cd_convenio 
into STRICT	cd_convenio_w 
from	protocolo_convenio a 
where	a.nr_seq_protocolo = nr_seq_protocolo_p;
 
select	coalesce(max(nr_seq_conta),'0') 
into STRICT	cd_retorno_w 
from	convenio_estrutura_conta 
where	cd_estrutura = (ie_emite_conta_p)::numeric  
and	cd_convenio = cd_convenio_w;
 
if (cd_retorno_w = '0') then 
		select	coalesce(max(nr_seq_conta),'0') 
		into STRICT	cd_retorno_w 
		from	convenio_estrutura_conta 
		where	cd_estrutura = (ie_emite_conta_p)::numeric  
		and	coalesce(cd_convenio::text, '') = '';
end if;
 
return cd_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
