CREATE OR REPLACE FUNCTION ufg_obter_status_ordem (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(30);
dt_baixa_w			timestamp;
dt_liberacao_w			timestamp;
dt_aprovacao_w			timestamp;
ie_possui_nota_w		varchar(1);
nr_seq_motivo_cancel_w		bigint;


BEGIN 
 
select	dt_baixa, 
	dt_liberacao, 
	dt_aprovacao, 
	substr(CASE WHEN obter_se_ordem_possui_nota(nr_ordem_compra)=0 THEN 'N'  ELSE 'S' END ,1,1) ie_possui_nota, 
	nr_seq_motivo_cancel 
into STRICT	dt_baixa_w, 
	dt_liberacao_w, 
	dt_aprovacao_w, 
	ie_possui_nota_w, 
	nr_seq_motivo_cancel_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (nr_seq_motivo_cancel_w IS NOT NULL AND nr_seq_motivo_cancel_w::text <> '') then 
	ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(298639); /* Cancelada*/
elsif (coalesce(dt_liberacao_w::text, '') = '') then 
	ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(298643); /* Pendente de Liberação */
elsif (coalesce(dt_aprovacao_w::text, '') = '') then 
	ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(298645); /* Pendente de aprovação */
elsif (ie_possui_nota_w = 'S') and (coalesce(dt_baixa_w::text, '') = '') then 
	ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(298647); /* Entrega parcial */
elsif (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then 
	ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(298654); /* Baixada */
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
