CREATE OR REPLACE FUNCTION ugf_obter_valores_faturar ( dt_mes_referencia_p timestamp, cd_conta_contabil_p text, cd_estab_p bigint, cd_tributo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision := 0;
vl_movto_conta_w	double precision;
vl_soma_movto_conta_w	double precision := 0;
pr_imposto_w		regra_calculo_imposto.pr_imposto%Type;
cd_conta_filtro_w	varchar(255) := cd_conta_contabil_p;
cd_conta_contabil_w	conta_contabil.cd_conta_contabil%Type;
ie_pos_w		bigint;

/*
ie_opcao_p

MAI - Movimento da conta até o mês com aplicação de pr_imposto;
MA

*/
BEGIN


	while (cd_conta_filtro_w IS NOT NULL AND cd_conta_filtro_w::text <> '') loop
		begin
		ie_pos_w	:= coalesce(position(';' in cd_conta_filtro_w),0);
		if (ie_pos_w = 0) then
			cd_conta_contabil_w	:= cd_conta_filtro_w;
			cd_conta_filtro_w		:= null;
		else
			cd_conta_contabil_w	:= substr(cd_conta_filtro_w,1,ie_pos_w-1);
			cd_conta_filtro_w	:= substr(cd_conta_filtro_w,ie_pos_w + 1,2000);
		end if;

		select 	sum(ctb_obter_valor_conta(a.nr_sequencia,cd_conta_contabil_w,b.cd_estabelecimento,null,'MA', null))
		into STRICT	vl_movto_conta_w
		from	ctb_mes_ref a,
			estabelecimento b
		where	tO_CHAR(a.dt_referencia,'MM/YYYY') = TO_CHAR(dt_mes_referencia_p,'MM/YYYY')
		and	b.cd_estabelecimento = cd_estab_p
		and	a.cd_empresa = b.cd_empresa;

		vl_soma_movto_conta_w := vl_soma_movto_conta_w + vl_movto_conta_w;
		end;
	end loop;

if (ie_opcao_p = 'MAI') then
	pr_imposto_w := obter_pr_imposto(cd_tributo_p) / 100;
	vl_retorno_w := vl_soma_movto_conta_w * pr_imposto_w;
elsif (ie_opcao_p = 'MA') then
	vl_retorno_w := vl_soma_movto_conta_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
