CREATE OR REPLACE FUNCTION usage_config_obter_parent_dlg ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w        objeto_schematic.nr_sequencia%type;


BEGIN

        SELECT  *
        INTO STRICT    nr_seq_retorno_w
        FROM (WITH RECURSIVE cte AS (
SELECT aa.nr_seq_obj_sup
                FROM objeto_schematic aa
LEFT OUTER JOIN objeto_schematic bb ON (aa.nr_seq_obj_sup = bb.nr_sequencia)
 WHERE aa.nr_sequencia = nr_sequencia_p
  UNION ALL
SELECT aa.nr_seq_obj_sup
                FROM objeto_schematic aa
LEFT OUTER JOIN objeto_schematic bb ON (aa.nr_seq_obj_sup = bb.nr_sequencia)
 JOIN cte c ON (c.prior nr_seq_obj_sup = aa.nr_sequencia)

) SELECT * FROM cte WHERE bb.ie_tipo_componente in ('WDLG','WCP') ORDER BY  level
                ;
) alias1
        WHERE   1 =1  LIMIT 1;

        return  nr_seq_retorno_w;

end;
$body$
