CREATE OR REPLACE FUNCTION usage_config_situacao_funcao ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w      			funcao.ie_situacao%type;


BEGIN

			select coalesce(max(a.ie_situacao),'N')
					into STRICT    retorno_w
					from    funcao a,
							funcao_schematic b
					where   b.nr_sequencia = nr_sequencia_p
                    and     a.cd_funcao = b.cd_funcao
					and     a.ie_situacao <> 'I'
					and (b.ie_situacao_funcao = 'A'
                            or (obter_se_base_wheb = 'S'
                            and b.ie_situacao_funcao = 'D'));

		return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
