SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION usage_config_visualiza_tab ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE

        pais_w   CHAR;
        global_w CHAR;

BEGIN
        SELECT  MAX(p.IE_VISUALIZA)
        INTO STRICT    pais_w
        FROM    tab_pais p
        WHERE   p.cd_pais        = obter_nr_seq_locale(wheb_usuario_pck.get_nm_usuario)
        AND     p.nr_seq_tab_ref = nr_sequencia_p;

        SELECT  MAX(p.IE_VISUALIZA)
        INTO STRICT    global_w
        FROM    tab_pais p
        WHERE   p.cd_pais        = 10
        AND     p.nr_seq_tab_ref = nr_sequencia_p;

        RETURN coalesce(coalesce(pais_w,global_w),'S');
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
