CREATE OR REPLACE FUNCTION user_can_delete_patient ( nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(1);
	

BEGIN 
select 	coalesce(max(ie_can_delete_patient), 'N') 
into STRICT 	ie_retorno_w 
from 	usuario 
where 	nm_usuario = nm_usuario_p 
and	cd_estabelecimento = cd_estabelecimento_p 
and	ie_situacao = 'A';
 
return	ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
