SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION user_can_view_sensitive_info ( nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
qt_grupos_w	bigint;


BEGIN
select 	coalesce(max(ie_can_view_sensitive_info), 'Y')
into STRICT 	ie_retorno_w
from 	usuario
where 	nm_usuario = nm_usuario_p
and	ie_situacao = 'A';

if (ie_retorno_w = 'N') then

	select 	count(*)
	into STRICT 	qt_grupos_w
	from 	usuario_grupo u,
		grupo_usuario g
	where 	u.nr_seq_grupo = g.nr_sequencia
	and	u.ie_situacao = 'A'
	and	g.ie_situacao = 'A'
	and	u.nm_usuario_grupo = nm_usuario_p
	and	g.cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(g.ie_can_view_sensitive_info, 'Y') = 'Y';

	if (qt_grupos_w > 0) then

		select 	coalesce(max(g.ie_can_view_sensitive_info), 'Y')
		into STRICT 	ie_retorno_w
		from 	usuario_grupo u,
			grupo_usuario g
		where 	u.nr_seq_grupo = g.nr_sequencia
		and	u.ie_situacao = 'A'
		and	g.ie_situacao = 'A'
		and	u.nm_usuario_grupo = nm_usuario_p
		and	g.cd_estabelecimento = cd_estabelecimento_p
		and	coalesce(g.ie_can_view_sensitive_info, 'Y') = 'Y';

	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
