CREATE OR REPLACE FUNCTION usuario_liberacao_service_pack (NM_USUARIO_P text) RETURNS varchar AS $body$
DECLARE

  usuario_liberado_w varchar(1) := 'N';

BEGIN
  if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
    if nm_usuario_p in ('rmcorrea', 'jrfusinato', 'jpweiss', 'cetrentin', 'afkirsten') then
      usuario_liberado_w := 'S';
    else
      begin
        select 'S'
          into STRICT usuario_liberado_w
          from usuario u
         where lower(u.nm_usuario) = lower(NM_USUARIO_P)
          and u.cd_setor_atendimento in (12,7,35,2);
      exception
        when no_data_found then
          usuario_liberado_w := 'N';
      end;
    end if;
  end if;

  return usuario_liberado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
