CREATE OR REPLACE FUNCTION validar_curp_rfc (CURP_P text, RFC_P text, CURP_GERADO_P text, RFC_GERADO_P text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(1);


BEGIN
  ds_retorno_w := 'N';
  if (OBTER_UTILIZA_NOM(wheb_usuario_pck.get_cd_estabelecimento) = 'S') and
      ((CURP_P IS NOT NULL AND CURP_P::text <> '') or (RFC_P IS NOT NULL AND RFC_P::text <> ''))then
    if (substr(CURP_P,1,length(CURP_GERADO_P)) <> CURP_GERADO_P ) or (substr(RFC_P,1,length(RFC_GERADO_P)) <> RFC_GERADO_P) then
      ds_retorno_w := 'S';
    end if;
  end if;

return ds_retorno_w;
end;
