CREATE OR REPLACE FUNCTION validar_duracao_horario_agenda (cd_agenda_p bigint, dt_horario_p timestamp, nr_min_duracao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_agenda_w	    agenda_paciente.nr_sequencia%type;
nr_min_orig_w		bigint;
ie_status_w		varchar(3);
nr_min_duracao_w	bigint;


BEGIN
nr_min_duracao_w := nr_min_duracao_p;
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '') then
	/* obter se existe horario */

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_agenda_w
	from	agenda_paciente
	where	cd_agenda = cd_agenda_p
	and	hr_inicio = dt_horario_p;

	/* obter status horario */

	if (nr_seq_agenda_w > 0) then
		select	ie_status_agenda,
			coalesce(nr_minuto_duracao,0)
		into STRICT	ie_status_w,
			nr_min_orig_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_w;

		/* validar duracao */

		if (ie_status_w = 'C') and (nr_min_orig_w <> nr_min_duracao_p) then
			nr_min_duracao_w := nr_min_orig_w;
		end if;
	end if;
end if;

return nr_min_duracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
