CREATE OR REPLACE FUNCTION validar_medico_assistente ( ds_lista_p text, nr_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE


	ds_curr	varchar(1000);

    c1 CURSOR FOR
    SELECT  distinct b.cd_pessoa_fisica cd_medico
    from    pessoa_fisica c, medico b, prescr_medica a
    where   b.cd_pessoa_fisica = c.cd_pessoa_fisica
    and     a.cd_prescritor = b.cd_pessoa_fisica
    and     a.nr_atendimento = nr_atendimento_p
    and     b.ie_situacao = 'A'
    
union

    SELECT  distinct b.cd_pessoa_fisica cd_medico
    from    pessoa_fisica c, medico b, evolucao_paciente a
    where   b.cd_pessoa_fisica = c.cd_pessoa_fisica
    and     a.cd_medico = b.cd_pessoa_fisica
    and     a.nr_atendimento = nr_atendimento_p
    and     b.ie_situacao = 'A'
    
union

    select  distinct b.cd_pessoa_fisica cd_medico
    from    pessoa_fisica c, medico b, atendimento_paciente a
    where   b.cd_pessoa_fisica = c.cd_pessoa_fisica
    and     a.cd_medico_referido = b.cd_pessoa_fisica
    and     a.nr_atendimento = nr_atendimento_p
    and     b.ie_situacao = 'A';


BEGIN
    open c1;
    loop
        fetch c1 into ds_curr;
        EXIT WHEN NOT FOUND; /* apply on c1 */
            begin
                if position(ds_curr  ds_lista_p) > 0 then
                    return 1;
                end if;
            end;
    end loop;

    return 0;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
