CREATE OR REPLACE FUNCTION validar_nota_exportada (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w varchar(2);
qtd_exp_w integer;


BEGIN

	select count(1)
	into STRICT qtd_exp_w
	from nota_fiscal  n,
		 nota_fiscal_exportada e
	where n.nr_seq_exportada = e.nr_sequencia
	and   (n.nr_seq_exportada IS NOT NULL AND n.nr_seq_exportada::text <> '')
	and   n.nr_sequencia = nr_sequencia_p;

	if (qtd_exp_w > 0) then
		retorno_w := 'S';
	else
		retorno_w := 'N';
	end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
