SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION validate_sms_response_text (ds_text_p text) RETURNS varchar AS $body$
DECLARE

ds_resp_w varchar(1) := 'O';


BEGIN

	select	CASE WHEN trim(both upper(ds_text_p))='YES' THEN 'S' WHEN trim(both upper(ds_text_p))='NO' THEN 'N' WHEN trim(both upper(ds_text_p))='SIM' THEN 'S' WHEN trim(both upper(ds_text_p))='NAO' THEN 'N'  ELSE 'O' END
	into STRICT	ds_resp_w
	;

	IF (ds_resp_w = 'O')	THEN

		select	coalesce(max(ie_tipo_retorno),'O')
		into STRICT	ds_resp_w
		from (

			SELECT	'S' ie_tipo_retorno
			
			where	trim(both upper(sem_acento(ds_text_p))) like '%SIM%'

			
union


			SELECT	'S' ie_tipo_retorno
			
			where	trim(both upper(sem_acento(ds_text_p))) like '%YES%'

			
union


			select	'N' ie_tipo_retorno
			
			where	trim(both upper(sem_acento(ds_text_p))) like '%NAO%'

			
union


			select	'N' ie_tipo_retorno
			
			where	trim(both upper(sem_acento(ds_text_p))) like '%NO%'
		) alias15;

	END IF;

	IF (ds_resp_w = 'O') THEN
		SELECT obter_se_resposta_Sim_cadastro(ds_text_p)
		INTO STRICT ds_resp_w
		;
	END IF;

RETURN ds_resp_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
