CREATE OR REPLACE FUNCTION valida_atividade_realizada ( nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE

qt_registros_w	integer;
ds_aviso_w	varchar(255) := null;

BEGIN
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from 	man_ordem_serv_ativ
	where	nr_seq_ordem_serv = nr_seq_ordem_p;
	if (qt_registros_w = 0) then
		select 	count(*)
		into STRICT	qt_registros_w
		from 	man_ordem_serv_ativ_pj
		where	nr_seq_ordem_servico = nr_seq_ordem_p;
		if (qt_registros_w = 0) then
			ds_aviso_w 	:= obter_texto_tasy(217859, 1);
		end if;
	end if;
end if;
return	ds_aviso_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
