SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION valida_dt_transicao_interface ( nr_seq_bomba_interface_p bigint ) RETURNS timestamp AS $body$
DECLARE

    dt_transicao_end_solution_w       timestamp;
    dt_transicao_ds_medicamento_w     timestamp;
    dt_transicao_return_w             timestamp;				

BEGIN

    select
        max(dt_transicao)
    into STRICT dt_transicao_end_solution_w
    from
        bomba_infusao_transicao
    where
								ds_campo = 'END_SOLUTION'
					   and nr_seq_bomba_interface = nr_seq_bomba_interface_p;
								
    select
        max(dt_transicao)
    into STRICT dt_transicao_ds_medicamento_w
    from
        bomba_infusao_transicao
    where
								ds_campo = 'DS_MEDICAMENTO'
					   and nr_seq_bomba_interface = nr_seq_bomba_interface_p;								


				if ((dt_transicao_end_solution_w IS NOT NULL AND dt_transicao_end_solution_w::text <> '') and coalesce(dt_transicao_end_solution_w, '01/01/2000') > coalesce(dt_transicao_ds_medicamento_w, '01/01/2000')) then
							select
											min(dt_transicao)
							into STRICT dt_transicao_return_w
							from
											bomba_infusao_transicao
							where	dt_transicao > dt_transicao_end_solution_w
									and dt_transicao > clock_timestamp() - interval '12 days'/24
									and nr_seq_bomba_interface = nr_seq_bomba_interface_p;
									end if;
					if ((dt_transicao_ds_medicamento_w IS NOT NULL AND dt_transicao_ds_medicamento_w::text <> '') and coalesce(dt_transicao_ds_medicamento_w, '01/01/2000') > coalesce(dt_transicao_end_solution_w, '01/01/2000')) then
							select
											min(dt_transicao)
							into STRICT dt_transicao_return_w
							from
											bomba_infusao_transicao
							where dt_transicao >= dt_transicao_ds_medicamento_w
         and dt_transicao > clock_timestamp() - interval '12 days'/24																		
									and nr_seq_bomba_interface = nr_seq_bomba_interface_p;
									end if;
					if (coalesce(dt_transicao_ds_medicamento_w::text, '') = '' and coalesce(dt_transicao_end_solution_w::text, '') = '') then
							select
											min(dt_transicao)
							into STRICT dt_transicao_return_w
							from
											bomba_infusao_transicao
							where
											dt_transicao > clock_timestamp() - interval '12 days'/24
											and nr_seq_bomba_interface = nr_seq_bomba_interface_p;
					end if;

    return dt_transicao_return_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION valida_dt_transicao_interface ( nr_seq_bomba_interface_p bigint ) FROM PUBLIC;

