CREATE OR REPLACE FUNCTION valida_fator_prognostico (nr_seq_estadio_p bigint, nr_seq_loco_reg_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w             	varchar(1) := 'S';
is_fator_existente_w		varchar(1);
qtd_fatores_prog_w           bigint;
qtd_fatores_prog_loco_w      bigint;
nr_seq_fator_regra_w         bigint;
nr_seq_valor_fator_regra_w   bigint;

C01 CURSOR FOR
	SELECT a.nr_seq_fator_prog, a.nr_seq_valor_fator_prog
	from regra_fator_prog a,
		 fator_prognostico b
	where a.nr_seq_can_estadio = nr_seq_estadio_p
	  and b.nr_sequencia = a.nr_seq_fator_prog
	  and b.ie_obrigatorio = 'S';


BEGIN

	select count(a.nr_sequencia)
	into STRICT qtd_fatores_prog_w
	from regra_fator_prog a,
		 fator_prognostico b
	where a.nr_seq_can_estadio = nr_seq_estadio_p
	  and b.nr_sequencia = a.nr_seq_fator_prog
	  and b.ie_obrigatorio = 'S';
	
	select count(a.nr_sequencia)
	into STRICT qtd_fatores_prog_loco_w
	from fator_prog_loco_reg a,
		 fator_prognostico b
	where a.nr_seq_can_loco_reg = nr_seq_loco_reg_p
	  and b.nr_sequencia = a.nr_seq_fator_prog
	  and b.ie_obrigatorio = 'S';
	
	if (qtd_fatores_prog_w > 0) then
		if (qtd_fatores_prog_w <> qtd_fatores_prog_loco_w) then
			ds_retorno_w := 'N';

		else
			open C01;
			loop
			fetch C01 into
				nr_seq_fator_regra_w,
				nr_seq_valor_fator_regra_w;
			exit when ((C01%notfound) or (ds_retorno_w = 'N'));
			
				select 'S'
				into STRICT is_fator_existente_w
				from fator_prog_loco_reg
				where nr_seq_fator_prog = nr_seq_fator_regra_w
					and nr_seq_valor_fator_prog = nr_seq_valor_fator_regra_w
					and nr_seq_can_loco_reg = nr_seq_loco_reg_p;

				if (is_fator_existente_w <> 'S') then
					ds_retorno_w := 'N';
				end if;
			
			end loop;
			close C01;
		end if;

	else
		if (qtd_fatores_prog_loco_w <> 0) then
			ds_retorno_w := 'N';
		end if;
	end if;

<<Fim>>

return  ds_retorno_w;

end;
$body$
