CREATE OR REPLACE FUNCTION valida_inicio_fim_adm ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE

ie_tipo_processo_w		varchar(1);
dt_inicio_w             timestamp;
dt_fim_w                timestamp;


BEGIN
ie_tipo_processo_w := 'P';

select 	max(a.dt_administracao),max(a.dt_fim_administracao)
into STRICT    dt_inicio_w, dt_fim_w
