CREATE OR REPLACE FUNCTION valida_medida_laudo ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


QT_MEDIDA_w	double precision;
nr_seq_medida_w	bigint;
QT_MAXIMO_w	double precision;
QT_MINIMO_w	double precision;
QT_MEDIDA_MAX_w	double precision;
QT_MEDIDA_MIN_w	double precision;


BEGIN

select	nr_seq_medida,
	qt_medida,
	qt_maximo,
	qt_minimo
into STRICT	nr_seq_medida_w,
	qt_medida_w,
	qt_maximo_w,
	qt_minimo_w
from	laudo_paciente_medida
where	nr_sequencia = nr_sequencia_p;

SELECT	coalesce(qt_minimo_w,QT_MEDIDA_MIN),
	coalesce(qt_maximo_w,QT_MEDIDA_MAX)
INTO STRICT	QT_MEDIDA_MIN_w,
	QT_MEDIDA_MAX_w
FROM	medida_exame_laudo
WHERE   nr_sequencia = nr_seq_medida_w;

if (qt_medida_W < QT_MEDIDA_MIN_w) or (qt_medida_W > QT_MEDIDA_MAX_w) then
	return	'N';
else
	return	'S';
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
