CREATE OR REPLACE FUNCTION valida_regra_arquivos ( nr_seq_tipo_arquivo_p bigint, cd_motivo_alta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w 			varchar(1) := 'N';
ie_regra_w 				integer;


BEGIN

	select 	count(1)
	into STRICT	ie_regra_w
	from	motivo_alta_tipo_arq
	where	nr_seq_tipo_arquivo = nr_seq_tipo_arquivo_p
	and 	cd_motivo_alta = cd_motivo_alta_p;

	if (ie_regra_w > 0) then
		ie_retorno_w := 'S';
	end if;

return	ie_retorno_w;

end;
$body$
