CREATE OR REPLACE FUNCTION valida_regra_cid ( nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE

 
ie_alta_cid_w			varchar(3);			
ie_consiste_cid_canc_w		varchar(1);
ds_erro_cid_w			varchar(255);
ie_se_cid_w			varchar(10);
ie_atend_cancelado_w		varchar(1) := 'N';
ds_retorno_w	 		varchar(2) := 'N';
cd_motivo_alta_w		bigint;


BEGIN 
 
	ie_alta_cid_w := Obter_param_Usuario(3111, 38, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_alta_cid_w);
	ie_consiste_cid_canc_w := Obter_param_Usuario(3111, 267, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_cid_canc_w);
 
	select 	max(cd_motivo_alta) 
	into STRICT	cd_motivo_alta_w 
	from	atendimento_paciente 
	where 	nr_atendimento = nr_atendimento_p;
	 
 
	if (ie_consiste_cid_canc_w = 'N') then 
 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_atend_cancelado_w 
		from	atendimento_cancelamento where		nr_atendimento = nr_atendimento_p LIMIT 1;
 
	end if;
 
if (ie_alta_cid_w = 'D') and 
		((ie_atend_cancelado_w = 'S' AND ie_consiste_cid_canc_w = 'N') or (ie_consiste_cid_canc_w = 'S')) then 
		ds_erro_cid_w := verifica_se_obriga_cid(nr_atendimento_p, cd_motivo_alta_w, ds_erro_cid_w);
	end if;
 
	select	obter_cid_atendimento(nr_atendimento_p,'P') 
	into STRICT	ie_se_cid_w 
	;
 
	 
	if (ds_erro_cid_w IS NOT NULL AND ds_erro_cid_w::text <> '') and (coalesce(ie_se_cid_w::text, '') = '') then 
		ds_retorno_w :='S';
	end if;
	 
	 
	return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
