CREATE OR REPLACE FUNCTION valida_regra_tecnico_laudo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w		bigint;
cd_tipo_procedimento_w		smallint;
ie_situacao_w			varchar(1);
cd_procedimento_w2		bigint;
cd_tipo_procedimento_w2		smallint;
nr_seq_proc_w			bigint;
nr_regras_w			integer;
ds_retorno_w			varchar(1);

c01 CURSOR FOR
	SELECT  cd_procedimento,
		cd_tipo_procedimento,
		ie_situacao
	FROM 	regra_tecnico_laudo
	WHERE	ie_situacao = 'A';


BEGIN
select 	count(*)
into STRICT	nr_regras_w
from	regra_tecnico_laudo
where	ie_situacao = 'A';
ds_retorno_w := 'N';
if (nr_regras_w > 0 AND nr_seq_laudo_p IS NOT NULL AND nr_seq_laudo_p::text <> '') then
	select 	nr_seq_proc
	into STRICT	nr_seq_proc_w
	from 	laudo_paciente
	where 	nr_sequencia = nr_seq_laudo_p;

	SELECT  a.cd_procedimento,
		b.cd_tipo_procedimento
	into STRICT	cd_procedimento_w2,
		cd_tipo_procedimento_w2
	FROM 	procedimento_paciente a,
		procedimento b
	WHERE 	a.nr_sequencia = nr_seq_proc_w
	AND	a.cd_procedimento = b.cd_procedimento
	AND	a.ie_origem_proced = b.ie_origem_proced;

	open c01;
	loop
	fetch c01 into	cd_procedimento_w,
			cd_tipo_procedimento_w,
			ie_situacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (coalesce(cd_tipo_procedimento_w::text, '') = '') then
			if (cd_procedimento_w = cd_procedimento_w2) then
				ds_retorno_w := 'S';
				exit;
			end if;
		elsif (cd_tipo_procedimento_w = cd_tipo_procedimento_w2) then
			ds_retorno_w := 'S';
			exit;
		end if;
	end loop;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
