CREATE OR REPLACE FUNCTION valor_bruto_ultima_compra ( cd_estabelecimento_p bigint, qt_dia_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, ie_nota_estoque_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
nr_sequencia_w	bigint;


BEGIN

vl_retorno_w		:= 0;

if (ie_nota_estoque_p = 'N') then
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	nota_fiscal_item a
	where	cd_material  		= cd_material_p
	and	dt_atualizacao	>= clock_timestamp() - coalesce(qt_dia_p,90)
	and	((coalesce(cd_local_estoque_p::text, '') = '') or (cd_local_estoque = cd_local_estoque_p))
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	exists (
		SELECT 1
		from	nota_fiscal b
		where	a.nr_sequencia	= b.nr_sequencia
		and	b.ie_situacao		= '1');
else
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	nota_fiscal_item a
	where	cd_material_estoque	= cd_material_p
	and	dt_atualizacao	>= clock_timestamp() - coalesce(qt_dia_p,90)
	and	((coalesce(cd_local_estoque_p::text, '') = '') or (cd_local_estoque = cd_local_estoque_p))
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	exists (
		SELECT 1
		from	nota_fiscal b
		where	a.nr_sequencia	= b.nr_sequencia
		and	b.ie_situacao		= '1');
end if;

if (nr_sequencia_w > 0) then
	select	coalesce(max(dividir(a.vl_total_item_nf, a.qt_item_estoque)),0)
	into STRICT	vl_retorno_w
	from 	nota_fiscal_item a
	where	nr_sequencia 		= nr_sequencia_w
	and	((ie_nota_estoque_p = 'N' AND cd_material = cd_material_p) or
		(ie_nota_estoque_p <> 'N' AND cd_material_estoque = cd_material_p));
end if;

return	vl_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
