CREATE OR REPLACE FUNCTION verificar_cpoe_ativa (cd_estabelecimento_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cpoe_ativo_w  varchar(1);


BEGIN

select coalesce(max('S'), 'N')
into STRICT ie_cpoe_ativo_w
from (SELECT  count(*) itens
         from    parametro_medico
         where   cd_estabelecimento = cd_estabelecimento_p
         and     ie_rep_cpoe = 'S'

union all

         SELECT  count(*) itens
         from    CPOE_MATERIAL 
         where   nr_atendimento = nr_atendimento_p) alias4
where   itens > 0;

return ie_cpoe_ativo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
