CREATE OR REPLACE FUNCTION verificar_existencia_pf (CD_PESSOA_FISICA_P pessoa_fisica.cd_pessoa_fisica%type) RETURNS PESSOA_FISICA.IE_SEXO%TYPE AS $body$
DECLARE


ie_existe_pf_w		pessoa_fisica.cd_pessoa_fisica%TYPE;


BEGIN
    SELECT
        (CASE WHEN (MAX(pf.cd_pessoa_fisica) IS NOT NULL AND (MAX(pf.cd_pessoa_fisica))::text <> '') THEN 'S' ELSE 'N' END)
    INTO STRICT
        ie_existe_pf_w
    FROM pessoa_fisica pf
    WHERE pf.cd_pessoa_fisica = cd_pessoa_fisica_p;

    RETURN ie_existe_pf_w;

END;
$body$
LANGUAGE PLPGSQL
