CREATE OR REPLACE FUNCTION verificar_existencia_prof_cm (cd_pessoa_fisica_p destinatario_carta_medica.cd_pessoa_fisica%type, nr_seq_carta_mae_p destinatario_carta_medica.nr_seq_carta_mae%type, cd_funcao_p text) RETURNS varchar AS $body$
DECLARE

  ie_retorno_w varchar(1);


BEGIN
    if (cd_funcao_p = '9960' )then
        select CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END 
        into STRICT ie_retorno_w
        from destinatario_carta_medica dcm
        where dcm.cd_pessoa_fisica = cd_pessoa_fisica_p
        and dcm.nr_seq_carta_mae = nr_seq_carta_mae_p;

    elsif (cd_funcao_p = '99010' )then
        select CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END 
        into STRICT ie_retorno_w
        from laudo_paciente_recipient lpr
        where lpr.cd_medico = cd_pessoa_fisica_p
        and lpr.nr_seq_laudo = nr_seq_carta_mae_p;

    end if;

    return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
