SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verificar_inconsistencias_nota ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);
ds_inconsistencia_w		varchar(255) := '';

c01 CURSOR FOR
SELECT 	coalesce(a.ds_consistencia,'XX')
from 	nota_fiscal_consist a
where 	a.nr_seq_nota = nr_sequencia_p
and 	a.ie_forma_consistencia = 'S';


BEGIN

	open c01;
	loop
	fetch c01 into
		ds_inconsistencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ds_inconsistencia_w <> 'XX') then
			ds_retorno_w := ds_retorno_w || ds_inconsistencia_w || chr(10);
		end if;
		end;
	end loop;
	close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
