CREATE OR REPLACE FUNCTION verificar_protocolo_ccg ( nr_seq_protocolo_p bigint, nr_prescricao_p bigint, nr_seq_procedimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_glic_inic_w		smallint;
qt_glic_fim_w		smallint;
qt_glic_inic_aux_w	bigint := null;
qt_glic_fim_aux_w	bigint := null;

C01 CURSOR FOR
	SELECT	a.qt_glic_inic,
		a.qt_glic_fim
	from	prescr_proc_glic a
	where	a.nr_seq_protocolo = nr_seq_protocolo_p
	and	a.nr_seq_procedimento = nr_seq_procedimento_p
	and	a.nr_prescricao = nr_prescricao_p
	and	ie_retorno_w = 'S'
	order by coalesce(qt_glic_inic,0);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	prescr_proc_glic b
where	b.nr_seq_protocolo = nr_seq_protocolo_p
and	b.nr_seq_procedimento = nr_seq_procedimento_p
and	b.nr_prescricao = nr_prescricao_p
and	b.qt_glic_fim = 999;

open C01;
loop
fetch C01 into
	qt_glic_inic_w,
	qt_glic_fim_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(qt_glic_inic_aux_w::text, '') = '') then
		begin
		qt_glic_inic_aux_w := qt_glic_inic_w;
		qt_glic_fim_aux_w  := qt_glic_fim_w + 1;
		if (qt_glic_inic_aux_w <> 0) then
			ie_retorno_w := 'N';
			Exit;
		end if;

		end;
	else
		begin
		if (qt_glic_fim_aux_w <> qt_glic_inic_w) then
			ie_retorno_w := 'N';
			Exit;
		else
			qt_glic_fim_aux_w := qt_glic_fim_w + 1;
		end if;
		end;
	end if;


	end;
end loop;
close C01;

return ie_retorno_w;

end;
$body$
