CREATE OR REPLACE FUNCTION verificar_registro_dominio_83 (IE_STATUS_AGENDA_P agenda_consulta.ie_status_agenda%type) RETURNS PESSOA_FISICA.IE_SEXO%TYPE AS $body$
DECLARE


ie_existe_registro_w		pessoa_fisica.cd_pessoa_fisica%TYPE;
dominio_verificacao constant valor_dominio.cd_dominio%TYPE := 83;


BEGIN
    SELECT
        (CASE WHEN (MAX(vl.vl_dominio) IS NOT NULL AND (MAX(vl.vl_dominio))::text <> '') THEN 'S' ELSE 'N' END)
    INTO STRICT
        ie_existe_registro_w
    FROM valor_dominio vl
    WHERE vl.cd_dominio = dominio_verificacao
    AND vl_dominio = IE_STATUS_AGENDA_P;

    RETURN ie_existe_registro_w;

END;
