CREATE OR REPLACE FUNCTION verificar_regra_operadora_web ( nr_seq_congenere_p bigint, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_gerar_cartao_w	varchar(1);
nr_seq_emissor_w	bigint;

-- Bloquear o campo da tarja no localizador de beneficiários congêneres no portal.
ds_retorno_w		varchar(1) := 'S';


BEGIN

select	ie_gerar_cartao,
	nr_seq_emissor
into STRICT	ie_gerar_cartao_w,
	nr_seq_emissor_w
from	pls_congenere
where	nr_sequencia	= nr_seq_congenere_p
and	ie_situacao	= 'A';

if (ie_gerar_cartao_w = 'S' and (nr_seq_emissor_w IS NOT NULL AND nr_seq_emissor_w::text <> '')) then
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
