CREATE OR REPLACE FUNCTION verificar_setor_exclusivo_ge (cd_procedimento_p text, ie_origem_proced_p bigint, cd_setor_atendimento_p bigint, nr_seq_proc_interno_p bigint, nm_usuario_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

qt_aux_w 		smallint;
ds_retorno_w		varchar(1) := 'S';
cd_setor_exclusivo_w	bigint;
ds_resultado_consulta_w		varchar(1);


BEGIN
  qt_aux_w := 0;
  select 	count(*)
    into STRICT	qt_aux_w
    from   	proc_interno_setor
    where  	nr_seq_proc_interno = nr_seq_proc_interno_p;

  if (nr_seq_proc_interno_p <> 0 and
       qt_aux_w > 0) then
    begin
      select 	coalesce(max('S'),'N')
      into STRICT	  ds_retorno_w
      from   	proc_interno_setor
      where  	nr_seq_proc_interno = nr_seq_proc_interno_p
      and    	cd_setor_atendimento = cd_setor_atendimento_p
      and     coalesce(cd_perfil, cd_perfil_p) = cd_perfil_p
      and     coalesce(ie_tipo_convenio, 0) = 0;
    end;
  else
    begin
      select 	coalesce(cd_setor_exclusivo,0)
      into STRICT	  cd_setor_exclusivo_w
      from 	  procedimento
      where 	cd_procedimento   = cd_procedimento_p
      and 	  ie_origem_proced  = ie_origem_proced_p;

      if (cd_setor_exclusivo_w > 0) then
        select 	coalesce(max('S'),'N')
        into STRICT	  ds_retorno_w
        from 	  usuario_setor_v b,
                procedimento a
        where   a.cd_setor_exclusivo = b.cd_setor_atendimento
        and 	  b.nm_usuario         = nm_usuario_p
        and 	  a.cd_procedimento    = cd_procedimento_p
        and 	  a.ie_origem_proced   = ie_origem_proced_p
        and 	  a.cd_setor_exclusivo = cd_setor_atendimento_p;
      else
        select 	coalesce(max('S'),'N')
        into STRICT	  ds_resultado_consulta_w
        from 	  procedimento_setor_atend a
        where 	a.cd_procedimento    = cd_procedimento_p
        and 	  a.ie_origem_proced   = ie_origem_proced_p
        and     coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;

        if (ds_resultado_consulta_w = 'S') then
          select 	coalesce(max('S'),'N')
          into STRICT	  ds_retorno_w
          from 	  usuario_setor_v b,
                  procedimento_setor_atend a
          where 	a.cd_setor_atendimento = b.cd_setor_atendimento
          and 	  b.nm_usuario         = nm_usuario_p
          and 	  a.cd_procedimento    = cd_procedimento_p
          and 	  a.ie_origem_proced   = ie_origem_proced_p
          and 	  a.cd_setor_atendimento = cd_setor_atendimento_p
          and     coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;
        end if;
      end if;
    end;
  end if;
  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
