CREATE OR REPLACE FUNCTION verificar_se_detento (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ie_retorno_w varchar(255);


BEGIN

    ds_retorno_w := obter_param_usuario(44, 191, OBTER_PERFIL_ATIVO, OBTER_USUARIO_ATIVO, OBTER_ESTABELECIMENTO_ATIVO, ds_retorno_w);

if ((ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and ds_retorno_w <> '0') then
	ie_retorno_w :=  obter_se_pac_detento(cd_pessoa_fisica_p,ds_retorno_w);
else
	ie_retorno_w := 'N';
end if;	

return	ie_retorno_w;

end;
