CREATE OR REPLACE FUNCTION verificar_se_recorr_serv (nr_seq_item_selec_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1);
qt_itens_recorr_serv_w		bigint;


BEGIN

if (nr_seq_item_selec_p IS NOT NULL AND nr_seq_item_selec_p::text <> '')then
	begin
	select	coalesce(count(*),0)
	into STRICT	qt_itens_recorr_serv_w
	from	agenda_integrada_item
	where	nr_seq_item_princ 	= nr_seq_item_selec_p
	and		ie_tipo_item 		= 'S'
	order by 1;

	if (qt_itens_recorr_serv_w > 0)then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

	exception
	when others then
		ds_retorno_w	:= 'N';
	end;
end if;




return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
