CREATE OR REPLACE FUNCTION verifica_alta_setor_usu_lib ( nm_usuario_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_setor_usuario_w		integer;
ie_permite_alta_w		varchar(1);
qt_regra_setor_usuario_w	bigint;
nr_seq_interno_w		bigint;
cd_setor_atendimento_w	integer;
cd_estabelecimento_w	smallint;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	count(*)
into STRICT	qt_regra_setor_usuario_w
from	regra_lib_alta_setor
where	ie_situacao = 'A'
and	cd_estabelecimento = cd_estabelecimento_w;

ie_permite_alta_w := 'S';

if (coalesce(qt_regra_setor_usuario_w,0) > 0) then

	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_usuario_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select	Obter_Atepacu_paciente(nr_atendimento_p,'A')
	into STRICT	nr_seq_interno_w
	;

	if (coalesce(nr_seq_interno_w,0) > 0) then

		select	max(cd_setor_atendimento)
		into STRICT	cd_setor_atendimento_w
		from 	atend_paciente_unidade
		where	nr_seq_interno = nr_seq_interno_w
		and	nr_atendimento = nr_atendimento_p;

		if (coalesce(cd_setor_atendimento_w,0) > 0) and (coalesce(cd_setor_usuario_w,0) > 0) then

			select	coalesce(max(ie_permite),'S')
			into STRICT	ie_permite_alta_w
			from	regra_lib_alta_setor
			where	coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w
			and	coalesce(cd_setor_usuario,cd_setor_usuario_w)         = cd_setor_usuario_w
			and	cd_estabelecimento                               = cd_estabelecimento_w;

		end if;
	end if;
end if;

return	ie_permite_alta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
