CREATE OR REPLACE FUNCTION verifica_anexo_email (nr_seq_correio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(001)	:= 'N';
ds_anexo_w	varchar(255)	:= '';


BEGIN

if (nr_seq_correio_p IS NOT NULL AND nr_seq_correio_p::text <> '') then
	begin

	select	ds_anexo
	into STRICT	ds_anexo_w
	from	usuario_correio
	where	nr_sequencia	= nr_seq_correio_p;

	if (ds_anexo_w IS NOT NULL AND ds_anexo_w::text <> '') then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
