CREATE OR REPLACE FUNCTION verifica_conta_estab ( cd_banco_p bigint, cd_agencia_bancaria_p text, nr_conta_p text, cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w	bigint;
qt_regra_estab_w bigint;
ds_retorno_w	varchar(1);


BEGIN

ds_retorno_w := 'S';

if (coalesce(cd_pessoa_fisica_p::text, '') = '') then

	select	count(*)
	into STRICT	qt_regra_w
	from	pessoa_jur_conta_estab
	where	cd_banco = cd_banco_p
	and	cd_agencia_bancaria = cd_agencia_bancaria_p
	and	nr_conta = nr_conta_p
	and	cd_cgc = cd_cgc_p;

	if (qt_regra_w > 0) then

		select	count(*)
		into STRICT	qt_regra_estab_w
		from	pessoa_jur_conta_estab
		where	cd_banco = cd_banco_p
		and	cd_agencia_bancaria = cd_agencia_bancaria_p
		and	nr_conta = nr_conta_p
		and	cd_cgc = cd_cgc_p
		and	cd_estabelecimento = cd_estabelecimento_p;

		if (qt_regra_estab_w = 0) then
			ds_retorno_w := 'N';
		end if;
	end if;
elsif (coalesce(cd_cgc_p::text, '') = '') then

	select	count(*)
	into STRICT	qt_regra_w
	from	pessoa_fis_conta_estab
	where	cd_banco = cd_banco_p
	and	cd_agencia_bancaria = cd_agencia_bancaria_p
	and	nr_conta = nr_conta_p
	and	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (qt_regra_w > 0) then

		select	count(*)
		into STRICT	qt_regra_estab_w
		from	pessoa_fis_conta_estab
		where	cd_banco = cd_banco_p
		and	cd_agencia_bancaria = cd_agencia_bancaria_p
		and	nr_conta = nr_conta_p
		and	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	cd_estabelecimento = cd_estabelecimento_p;

		if (qt_regra_estab_w = 0) then
			ds_retorno_w := 'N';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
