CREATE OR REPLACE FUNCTION verifica_convenio_vigente ( nr_atendimento_p atend_categoria_convenio.cd_convenio%type, cd_pessoa_fisica_p pessoa_titular_convenio.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE

					
qt_reg_w	bigint;
ie_retorno_w	varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
	ie_retorno_w := 'N';
	qt_reg_w := 0;
	
	select  count(*)
	into STRICT	qt_reg_w
	from    atend_categoria_convenio ac
	where   ac.nr_atendimento = nr_atendimento_p
	and     ac.cd_convenio in (	SELECT  pt.cd_convenio
					from    pessoa_titular_convenio pt
					where   pt.cd_pessoa_fisica = cd_pessoa_fisica_p
					and (trunc(pt.dt_inicio_vigencia) <= trunc(clock_timestamp())
					and (trunc(pt.dt_fim_vigencia) >= trunc(clock_timestamp()) or coalesce(pt.dt_fim_vigencia::text, '') = '')));
					
	if (qt_reg_w = 0) then
		select 	count(1)
		into STRICT	qt_reg_w
		from    pessoa_titular_convenio pt
		where   pt.cd_pessoa_fisica = cd_pessoa_fisica_p
		and (trunc(pt.dt_inicio_vigencia) <= trunc(clock_timestamp())
		and (trunc(pt.dt_fim_vigencia) >= trunc(clock_timestamp()) or coalesce(pt.dt_fim_vigencia::text, '') = ''))
		and     pt.cd_convenio not in ( SELECT  ac.cd_convenio
						from    atend_categoria_convenio ac
						where   ac.nr_atendimento = nr_atendimento_p);
					
		if (qt_reg_w > 0) then
			ie_retorno_w := 'S';
		end if;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
