SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_coor_gerente_proj (nr_seq_proj_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);
cd_coordenador_w varchar(20);
cd_equipe_funcao_w bigint;
qt_gerente_w bigint;


BEGIN

ds_retorno_w:= 'N';

select max(cd_coordenador)
into STRICT cd_coordenador_w
from proj_projeto
where nr_sequencia = nr_seq_proj_p;

if (cd_coordenador_w = cd_pessoa_fisica_p) then
	ds_retorno_w:= 'S';
end if;

select	count(*)
into STRICT	qt_gerente_w
from	proj_equipe a,
	proj_equipe_papel b
where	a.nr_sequencia = b.nr_Seq_equipe
and	a.NR_SEQ_EQUIPE_FUNCAO = 10
and	b.NR_SEQ_FUNCAO = 51
and	a.nr_seq_proj = nr_seq_proj_p
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p;

if (qt_gerente_w > 0) then
	ds_retorno_w:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
