CREATE OR REPLACE FUNCTION verifica_cotacao_moeda_caixa (nr_seq_caixa_p bigint, dt_saldo_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_existe_cotacao_w	varchar(1) := 'N';
cd_moeda_w		integer;
qt_cotacao_w		integer := 0;

c01 CURSOR FOR
SELECT	cd_moeda
from	caixa_moedas
where	nr_seq_caixa = nr_seq_caixa_p;


BEGIN
open c01;
loop
fetch c01 into
	cd_moeda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	count(*)
	into STRICT	qt_cotacao_w
	from	cotacao_moeda
	where	cd_moeda = cd_moeda_w
	and	trunc(dt_cotacao,'dd') = trunc(coalesce(dt_saldo_p,clock_timestamp()),'dd')
	and	coalesce(coalesce(vl_cotacao_paral,vl_cotacao),0) > 0;

	if qt_cotacao_w > 0 then
		ie_existe_cotacao_w := 'S';
	else
		ie_existe_cotacao_w := 'N';
	end if;
	qt_cotacao_w := 0;
	end;
end loop;

return ie_existe_cotacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
