SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_encaixe_turno_agenda (cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_encaixe_w		varchar(01) := 'S';
dt_inicial_w		timestamp;
dt_final_w		timestamp;
ie_encaixe_turno_w	varchar(01);
ie_ordem_w		integer;

c01 CURSOR FOR
	SELECT	1 ie_ordem,
		to_date(to_char(dt_agenda_p, 'dd/mm/yyyy') || ' ' || to_char(hr_inicial, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss'),
		to_date(to_char(dt_agenda_p, 'dd/mm/yyyy') || ' ' || to_char(hr_final, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss'),
		ie_encaixe
	from	agenda_turno
	where	cd_agenda 	= cd_agenda_p
	and	ie_dia_semana	= 9
	
union

	SELECT	2 ie_ordem,
		to_date(to_char(dt_agenda_p, 'dd/mm/yyyy') || ' ' || to_char(hr_inicial, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss'),
		to_date(to_char(dt_agenda_p, 'dd/mm/yyyy') || ' ' || to_char(hr_final, 'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss'),
		ie_encaixe
	from	agenda_turno
	where	cd_agenda 	= cd_agenda_p
	and	ie_dia_semana	= pkg_date_utils.get_WeekDay(dt_agenda_p)
	order by 1;



BEGIN

open	c01;
loop
fetch	c01 into
	ie_ordem_w,
	dt_inicial_w,
	dt_final_w,
	ie_encaixe_turno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (dt_inicial_w <= dt_agenda_p) and (dt_final_w  >= dt_agenda_p) then
		ie_encaixe_w	:= ie_encaixe_turno_w;
	end if;


	end;
end loop;
close c01;


return ie_encaixe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
