SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_equipe_cronograma ( nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_equipe_w		bigint;


BEGIN
	ie_retorno_w := 'F';

	select	count(a.nr_sequencia)
	into STRICT	qt_equipe_w
	from	proj_cron_etapa_equipe a
	where	a.nr_seq_etapa_cron = nr_seq_etapa_p;

	if (qt_equipe_w = 0) then
		ie_retorno_w := 'T';
	else
		select	count(b.nr_sequencia)
		into STRICT	qt_equipe_w
		from	proj_cron_etapa_equipe b
		where	b.nr_seq_etapa_cron = nr_seq_etapa_p
		and	coalesce(b.ie_recurso_externo,'N') = 'N';

		if qt_equipe_w > 0 then
			ie_retorno_w := 'T';
		end if;
	end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
