SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_estab_pac_age (nr_seq_ageint_p bigint) RETURNS varchar AS $body$
DECLARE

 
 ie_bloq_estab_diferente_w   varchar(1);
 retorno_w           varchar(2);
 cd_estabelecimento_agenda_w	 bigint;
 cd_estabelecimento_paciente_w	bigint;
 cd_agenda_w				      bigint;

BEGIN
 retorno_w := 'OK';
  
 select max(cd_agenda) 
  into STRICT cd_agenda_w 
  from ageint_marcacao_usuario 
  where nr_seq_ageint = nr_seq_ageint_p;
  
 select max(cd_estabelecimento), max(coalesce(ie_bloq_estab_diferente,'N')) 
  into STRICT cd_estabelecimento_agenda_w, ie_bloq_estab_diferente_w 
  from agenda 
  where cd_agenda = cd_agenda_w;
  
 select max(pessoa_fisica.cd_estabelecimento) 
  into STRICT cd_estabelecimento_paciente_w 
  from pessoa_fisica 
  inner join agenda_integrada on agenda_integrada.cd_pessoa_fisica = pessoa_fisica.cd_pessoa_fisica and agenda_integrada.nr_sequencia = nr_seq_ageint_p;
  
 
 if ((cd_estabelecimento_agenda_w IS NOT NULL AND cd_estabelecimento_agenda_w::text <> '') and (cd_estabelecimento_paciente_w IS NOT NULL AND cd_estabelecimento_paciente_w::text <> '') and (cd_estabelecimento_agenda_w <> cd_estabelecimento_paciente_w)) then 
   retorno_w := ie_bloq_estab_diferente_w;
 end if;
 
 return retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION verifica_estab_pac_age (nr_seq_ageint_p bigint) FROM PUBLIC;

