SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_estadia_higienizacao ( nr_atendimento_p bigint, nr_seq_unidade_p bigint, qt_estadia_leito_p bigint) RETURNS varchar AS $body$
DECLARE

/*
S = Necessita Higienização
N = Não necessita Higienização
*/
ds_retorno_leito_w	varchar(1);
ds_retorno_setor_w	varchar(1);
ds_retorno_w		varchar(1);
dt_entrada_w	timestamp;
dt_ultima_hig_w	timestamp;
qt_dias_w	integer;
cd_setor_atendimento_w	bigint;
nr_seq_unidade_w	bigint;


BEGIN
if (coalesce(nr_seq_unidade_p,0) > 0) then
	nr_seq_unidade_w := nr_seq_unidade_p;

else

	select	max(nr_seq_interno)
	into STRICT	nr_seq_unidade_w
	from 	unidade_atendimento
	where 	nr_atendimento = nr_atendimento_p;
end if;

if (coalesce(nr_seq_unidade_w,0) > 0) then

	if (coalesce(nr_atendimento_p,0) > 0) then

		select	max(cd_setor_atendimento)
		into STRICT	cd_setor_atendimento_w
		from	unidade_atendimento
		where	nr_seq_interno = nr_seq_unidade_w;

		select	Max(dt_entrada_unidade)
		into STRICT	dt_entrada_w
		from	atend_paciente_unidade b
		where	b.nr_atendimento = nr_atendimento_p
		and	b.cd_setor_atendimento = cd_setor_atendimento_w;

		qt_dias_w := obter_dias_entre_datas(dt_entrada_w, clock_timestamp());
	else
		qt_dias_w := coalesce(qt_estadia_leito_p,0) - 1;
	end if;

	if (qt_dias_w >= coalesce(qt_estadia_leito_p,0)) then
		ds_retorno_leito_w := 'S';
	else
		ds_retorno_leito_w := 'N';
	end if;

	select 	coalesce(max(b.dt_atualizacao),clock_timestamp())
	into STRICT	dt_ultima_hig_w
	from	sl_unid_atend b
	where	b.nr_seq_unidade = nr_seq_unidade_w;

	qt_dias_w := obter_dias_entre_datas(dt_ultima_hig_w,clock_timestamp());
	--sysdate - dt_ultima_hig_w;
	if (qt_dias_w >= coalesce(qt_estadia_leito_p,0)) then
		ds_retorno_setor_w := 'S';
	else
		ds_retorno_setor_w := 'N';
	end if;

	if (ds_retorno_leito_w = 'S') and (ds_retorno_setor_w = 'S') then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
