CREATE OR REPLACE FUNCTION verifica_exame_duplic_dia ( nr_seq_exame_p bigint, cd_procedimento_p bigint, dt_prev_execucao_p timestamp, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := '';

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (dt_prev_execucao_p IS NOT NULL AND dt_prev_execucao_p::text <> '') then

	select	substr(Obter_Desc_Exame(max(b.nr_seq_exame)),1,200)
	into STRICT	ds_retorno_w
	from	prescr_procedimento b,
			prescr_medica a
	where	b.cd_procedimento = cd_procedimento_p
	and		b.nr_seq_exame = nr_seq_exame_p
	and		trunc(b.dt_prev_execucao) = trunc(dt_prev_execucao_p)
	and		((b.nr_sequencia <> coalesce(nr_seq_procedimento_p,0)) or (b.nr_prescricao <> coalesce(nr_prescricao_p,0)))
	and		coalesce(b.dt_suspensao::text, '') = ''
	and		a.nr_prescricao = b.nr_prescricao
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		dt_prev_execucao_p between a.dt_inicio_prescr and a.dt_validade_prescr
	and		a.nr_atendimento = nr_atendimento_p;

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		--O exame #@DS_EXAME#@ j√° foi prescrito neste dia para o paciente!
		ds_retorno_w	:= substr(obter_texto_dic_objeto(219312, 1, 'DS_EXAME='||ds_retorno_w||';'),1,255);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
