CREATE OR REPLACE FUNCTION verifica_exclusao_tit_receber (nr_adiantamento_p bigint) RETURNS varchar AS $body$
DECLARE

qt_registros_w	bigint;
ds_erro_w	varchar(100) := '';

BEGIN
if (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') then
	begin
	select  	count(*)
        	into STRICT	qt_registros_w
	from    	movto_trans_financ
	where   	nr_adiantamento = nr_adiantamento_p;
	if (nr_adiantamento_p <> 0) then
		begin
		ds_erro_w := obter_texto_tasy(174349, 1);
		end;
	end if;
	end;
end if;
return	ds_erro_w;
end;
$body$
LANGUAGE PLPGSQL
