SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_existe_agrup_uni (cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE


nr_agrupamento_w	integer;
ie_unidade_disp_w	varchar(1);
qt_unid_agrup_hig_w	bigint;
cd_tipo_acomodacao_w	smallint;


BEGIN

ie_unidade_disp_w := 'S';

if (coalesce(cd_setor_atendimento_p,0) > 0) then

	select	coalesce(max(nr_agrupamento),0),
		max(cd_tipo_acomodacao)
	into STRICT	nr_agrupamento_w,
		cd_tipo_acomodacao_w
	from	unidade_atendimento
	where 	cd_setor_atendimento = cd_setor_atendimento_p
	and   	cd_unidade_basica = cd_unidade_basica_p
	and   	cd_unidade_compl =  cd_unidade_compl_p;

	if (nr_agrupamento_w > 0) then

		select	count(*)
		into STRICT	qt_unid_agrup_hig_w
		from	unidade_atendimento
		where 	cd_setor_atendimento = cd_setor_atendimento_p
		and	nr_agrupamento = nr_agrupamento_w
		and     cd_tipo_acomodacao <> cd_tipo_acomodacao_w
		and	ie_status_unidade = 'H';

		if (qt_unid_agrup_hig_w > 0) then

			ie_unidade_disp_w := 'N';

		end if;
	end if;

end if;

return	ie_unidade_disp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
